export const availableOperations = new Set(OPERATION_ENTRIES.map((entry) => `${entry.method} ${entry.pathTemplate}`))

export function normalizeContractPath(rawPath: string): string {
  if (!rawPath) return '/'
  let path = rawPath
  try {
    if (path.startsWith('http://') || path.startsWith('https://')) {
      path = new URL(path).pathname
    }
  } catch {
    // Keep raw value for downstream normalization.
  }
  path = path.split('?')[0] || '/'
  if (!path.startsWith('/')) path = '/' + path
  path = path.replace(/\/+/g, '/')
  return path
}

export function isOperationAvailable(method: string, path: string): boolean {
  const normalizedMethod = method.toUpperCase()
  const normalizedPath = normalizeContractPath(path)
  return OPERATION_ENTRIES.some((entry) => entry.method === normalizedMethod && entry.regex.test(normalizedPath))
}

export function getMissingOperations(operations: Array<{ method: string; path: string }>): string[] {
  return operations
    .filter((operation) => !isOperationAvailable(operation.method, operation.path))
    .map((operation) => `${operation.method.toUpperCase()} ${normalizeContractPath(operation.path)}`)
}
